import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Top from './top.js';
import Skills from './skills.js';
import Work from './work.js';
import Projects from './projects.js';
import Education from './education.js';
import Footer from './page_footer.js';

import Paper from '@material-ui/core/Paper';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';


const useStyles = makeStyles(theme => ({
  base_nav: {
    marginTop: '2vh',
    marginBottom: '2vh',
    textAlign: 'center',
    width: '65%',
  },
  menu_button: {
    justifyContent: 'center',
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  paper: {
    backgroundColor: '#87A4FF',
    position: 'fixed',
    top: '0',
    left: '16.7%',
  },
  img_item: {
    alignItems: 'stretch',
  }
}));


export default function HeadBar() {
  const classes = useStyles();
  const [value, setValue] = React.useState();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const list = [
    {'id': '0', 'template': <Top />},
    {'id': '1', 'template': <Skills />},
    {'id': '2', 'template': <Work />},
    {'id': '3', 'template': <Projects />},
    {'id': '4', 'template': <Education />},
    {'id': '5', 'template': <Footer />},
  ];
  const refs = list.reduce((acc, value) => {
    acc[value.id] = React.createRef();
    return acc;
  }, {});

  const handleChange = (event, index) => {
    setValue(index);
    refs[index].current.scrollIntoView({
      behavior: 'smooth',
      block: 'start',
    });
  };

  const handleToggle = () => {
    setMobileOpen(!mobileOpen)
  }

  return (
    <div className={classes.base_nav}>
      <Paper className={classes.paper}>
        <IconButton onClick={handleToggle} className={classes.menu_button}>
          <MenuIcon/>
        </IconButton>
        <Tabs
        value={value}
        onChange={handleChange}
        indicatorColor="primary"
        textColor="primary"
        centered
        >
          <Tab label="Top" />
          <Tab label="Skills" />
          <Tab label="Work" />
          <Tab label="Projects" />
          <Tab label="Education" />
          <Tab label="Bottom" />
        </Tabs>
      </Paper>

      <Paper elevation={0}>
        {list.map(item => (
          <Paper key={item.id} ref={refs[item.id]} elevation={0} className={classes.img_item}>
            {item.template}
          </Paper>
        ))}
      </Paper>
    </div>
  );
}
